// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è —Å–ø–∏—Å–∫–∞ –≥–ª–∞–≤
function displayChapters() {
    const chaptersList = document.getElementById('chapters-list');
    chaptersList.innerHTML = '';

    bookData.chapters.forEach(chapter => {
        const chapterElement = document.createElement('a');
        chapterElement.className = 'list-group-item list-group-item-action';
        chapterElement.textContent = chapter.title;
        chapterElement.addEventListener('click', () => displayChapter(chapter));
        chaptersList.appendChild(chapterElement);
    });
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ –≥–ª–∞–≤—ã
function displayChapter(chapter) {
    const chapterTitle = document.getElementById('current-chapter-title');
    const chapterContent = document.getElementById('chapter-content');
    
    chapterTitle.textContent = chapter.title;
    chapterContent.textContent = chapter.content;

    // –û–±–Ω–æ–≤–ª—è–µ–º –∞–∫—Ç–∏–≤–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –≤ —Å–ø–∏—Å–∫–µ –≥–ª–∞–≤
    document.querySelectorAll('.list-group-item').forEach(item => {
        item.classList.remove('active');
        if (item.textContent === chapter.title) {
            item.classList.add('active');
        }
    });
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è —Ç–µ–º—ã
function toggleTheme() {
    document.body.classList.toggle('light-theme');
    const themeIcon = document.querySelector('#theme-toggle i');
    themeIcon.classList.toggle('fa-moon');
    themeIcon.classList.toggle('fa-sun');
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ —à—Ä–∏—Ñ—Ç–∞
let fontSize = 1.1;
function toggleFontSize() {
    fontSize = fontSize === 1.1 ? 1.3 : 1.1;
    document.getElementById('chapter-content').style.fontSize = `${fontSize}rem`;
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–ª–∏—Å—Ç—ã–≤–∞–Ω–∏—è —Å—Ç—Ä–∞–Ω–∏—Ü—ã
function turnPage() {
    const bookPage = document.querySelector('.book-page');
    const pageContent = document.querySelector('.book-page-content');
    
    // –î–æ–±–∞–≤–ª—è–µ–º –∫–ª–∞—Å—Å –¥–ª—è –∞–Ω–∏–º–∞—Ü–∏–∏ –ø–µ—Ä–µ–ª–∏—Å—Ç—ã–≤–∞–Ω–∏—è
    bookPage.classList.add('page-turn');
    
    // –ü–æ—Å–ª–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∞–Ω–∏–º–∞—Ü–∏–∏
    setTimeout(() => {
        // –£–±–∏—Ä–∞–µ–º –∫–ª–∞—Å—Å –∞–Ω–∏–º–∞—Ü–∏–∏
        bookPage.classList.remove('page-turn');
        
        // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
        pageContent.classList.remove('show');
        setTimeout(() => {
            // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –∑–∞–≥—Ä—É–∑–∏—Ç—å –Ω–æ–≤–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ
            pageContent.classList.add('show');
        }, 100);
    }, 1500);
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –∫–Ω–æ–ø–∫–∏ –ø–µ—Ä–µ–ª–∏—Å—Ç—ã–≤–∞–Ω–∏—è
document.getElementById('next-page').addEventListener('click', turnPage);

// –§—É–Ω–∫—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ –≥–ª–∞–≤—ã
function loadChapter(chapterId) {
    const pageContent = document.querySelector('.book-page-content');
    pageContent.classList.remove('show');
    
    // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –∑–∞–≥—Ä—É–∑–∫—É –Ω–æ–≤–æ–≥–æ —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ
    setTimeout(() => {
        pageContent.classList.add('show');
    }, 500);
}

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
document.addEventListener('DOMContentLoaded', () => {
    displayChapters();
    
    // –î–æ–±–∞–≤–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –¥–ª—è –∫–Ω–æ–ø–æ–∫
    document.getElementById('theme-toggle').addEventListener('click', toggleTheme);
    document.getElementById('font-size-toggle').addEventListener('click', toggleFontSize);
}); 